import './App.css';
import React,{useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function App() {
  const [nombre , setNombre]= useState("")
  const [numberError,setNumericError]= useState("")

 
  const cambiarNombre=(e) =>{
    const value=e.target.value;
    console.log(value);
    const minValue=value.length >3;
    const maxValue=value.length < 10;
    const onliLet = /^[a-zA-Z\s]*$/g.test(value);//PARA QUE SOLO HAYA LETRAS
    
    if(onliLet===false) {
      setNumericError(1);
    }else if(!minValue) {
      setNumericError(2);
    }else{
      setNumericError(3);
    }

    if(onliLet===true && minValue && maxValue) {
      setNumericError(0)
    }
    setNombre(value);
  }

    const guardarClick = () => {
      
      alert("Este es mi estado local",nombre);
    }

      return (
        <div className='App'>
           <header className="App-header">
           <div className="alert alert-success" role="alert">
          <h4 className="alert-heading">MI PRIMER SISTEMA CON REACT</h4>
        </div>
           
           <div className="card-body">

          <h2>Formulario Ventas</h2>
          <Form.Group className="mb-3" >
            <Form.Label>Cliente </Form.Label>
            <Form.Control  id="nombre" name="nombre" value={nombre}
            onChange={cambiarNombre} 
            type="text" placeholder="Ingrese su nombre" />
               {numberError === 1 && (

                  <label className="text_error">

                  El nombre solo se puede incluir letras

                  </label>

                    )}
                    {numberError === 2 && (

                <label className="text_error">

                  El nombre minimo es 3 caracteres

                </label>

                )}



                {numberError === 3 && (

                <label className="text_error">

                  El nombre maximo es 10 caracteres

                </label>

                )}
          
          </Form.Group>
          <Form.Group className="mb-3" >
            <Form.Label>Email</Form.Label>
            <Form.Control type="number" id="email" placeholder="Ingrese email"
            />
            

            
          </Form.Group>
    
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="text" placeholder="Password"  
            />
            
          </Form.Group>

          <Button  disabled={ numberError>0  } onClick={guardarClick} variant="primary">Guardar</Button>{' '}
          
          </div>
          </header>

          </div>

      
          )
  }

export default App
