import './App.css';
import React,{useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function App() {
  const [nombre , setNombre]= useState("")
  const [numberError,setNumericError]= useState("")

  const cambiarNombre=(e) =>{
    const value=e.target.value;
    console.log(value);
    const minValue=value.length >3;
    const maxValue=value.length < 10;
    const onliLet = /^[a-zA-Z\s]*$/g.test(value);//PARA QUE SOLO HAYA LETRAS
    console.log("Min:", minValue);
    console.log("Max:", maxValue);
    console.log("onliLet:", onliLet);  
    
    if(onliLet===false) {
      setNumericError(1);
    }else if(!minValue) {
      setNumericError(2);
    }else{
      setNumericError(3);
    }

    if(onliLet===true && minValue && maxValue) {
      setNumericError(0)
    }
    setNombre(value);
  }
    const guardarClick = () => {
      
      console.log("Este es mi estado local",nombre);
    }

      return (
        <Form>
          <h2>Mi Formulario</h2>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Nombre </Form.Label>

            <Form.Control  id="nombre"

                  name="nombre"

                  value={nombre}

                  onChange={cambiarNombre} type="text" placeholder="Ingrese su nombre" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Ingrese email" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>
    
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>

          <Button  disabled={numberError>0} onClick={guardarClick} variant="primary">Primary</Button>{' '}

      </Form>

      )
  }

export default App
