import Container from '@mui/material/Container';
import React, { useEffect, useState } from 'react';


function Contacto() {
    const [isLoading, setIsLoading] = useState(true);
    const [imageUrl, setImageUrl] = useState(null);
    const [contador, setContador] = useState(0);
    const [intervaloId, setIntervaloId] = useState(null); // Nuevo estado para guardar el ID del intervalo
  
    const obtenerImagenAleatoria = () => {
      const timestamp = new Date().getTime();
      const url = `https://randomfox.ca/floof/?ref=apilist.fun${timestamp}`;
  
      fetch(url)
        .then((response) => response.json())
        .then((data) => {
          setImageUrl(data.image);
          setIsLoading(false);
        });
    };
  
    const iniciarIntervalo = () => {
      const intervalo = setInterval(() => {
        setContador((conta) => conta + 1);
        obtenerImagenAleatoria();
      }, 2000);
      setIntervaloId(intervalo); // Guarda el ID del intervalo
    };
  
    const detenerIntervalo = () => {
      clearInterval(intervaloId); // Detiene el intervalo
    };
  
    useEffect(() => {
      iniciarIntervalo(); // Comienza el intervalo al cargar el componente
  
      return () => {
        // Limpia el intervalo cuando el componente se desmonta
        clearInterval(intervaloId);
      };
    }, []); // El efecto se ejecuta solo al montar el componente
  
    return (
      <Container maxWidth="md">
        <h2>Contacto</h2>
        <div>
          <h2>GET Example</h2>
          {isLoading ? (
            <p>Cargando...</p>
          ) : (
            <img src={imageUrl} alt="Random Fox" />
          )}
          <div>
            <button onClick={detenerIntervalo}>Detener actualizaci√≥n</button>
          </div>
        </div>
      </Container>
    );
  }
  
  export default Contacto;
