import './App.css';
import React,{useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function App() {
  const [nombre , setNombre]= useState("")
  const [numberError,setNumericError]= useState("")
  const [email,setEmail]= useState("")
  const [emailError,setErrorEmail]= useState("")


  const typeEmail= (e) =>{
    const value=e.target.value;
    const validEmail=/^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/;

    if (validEmail ===false){
      setErrorEmail(1)
    }
    if (validEmail ===true){
      setErrorEmail(0)
    }
    setEmail(value)

  }
 
    const guardarClick = () => {
      
      alert("Este es mi estado local",nombre,email);
    }

      return (
        <Form>
          <h2>Mi Formulario</h2>
          <Form.Group className="mb-3" >
            <Form.Label>Nombre </Form.Label>
            <Form.Control  id="nombre" name="nombre" value={nombre}
            onChange={cambiarNombre} 
            type="text" placeholder="Ingrese su nombre" />

               {numberError === 1 && (

                  <label className="text_error">

                  El nombre solo se puede incluir letras

                  </label>

                    )}
                    {numberError === 2 && (

                <label className="text_error">

                  El nombre minimo es 3 caracteres

                </label>

                )}



                {numberError === 3 && (

                <label className="text_error">

                  El nombre maximo es 10 caracteres

                </label>

                )}
          
          </Form.Group>
          <Form.Group className="mb-3" >
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Ingrese email" value={email} 
            onChange={typeEmail}/>
          
          </Form.Group>
    
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          
          </Form.Group>

          <Button  disabled={numberError>0 || emailError>0 } onClick={guardarClick} variant="primary">Primary</Button>{' '}

      </Form>

      
          )
  }

export default App
