import './App.css';
import React,{useState} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function App() {
  const [nombre , setNombre]= useState("")
  const [numberError,setNumericError]= useState("")
  const [emailError,setEmailError]= useState("")

  const cambiarNombre=(e) =>{
    const value=e.target.value;
    console.log(value);
    const minValue=value.length >3;
    const maxValue=value.length < 10;
    const onliLet = /^[a-zA-Z\s]*$/g.test(value);//PARA QUE SOLO HAYA LETRAS
    const validEmail=/^\w+([.-_+]?\w+)*@\w+([.-]?\w+)*(\.\w{2,10})+$/;
    
    if(onliLet===false) {
      setNumericError(1);
    }else if(!minValue) {
      setNumericError(2);
    }else{
      setNumericError(3);
    }

    if(onliLet===true && minValue && maxValue) {
      setNumericError(0)
    }
    setNombre(value);

    if (validEmail===false) {
      setEmailError(4);
    }
  }
    const guardarClick = () => {
      
      console.log("Este es mi estado local",nombre);
    }

      return (
        <Form>
          <h2>Mi Formulario</h2>
          <Form.Group className="mb-3" >
            <Form.Label>Nombre </Form.Label>
            <Form.Control  id="nombre" name="nombre" value={nombre}
            onChange={cambiarNombre} 
            type="text" placeholder="Ingrese su nombre" />
               {numberError === 1 && (

                  <label className="text_error">

                  El nombre solo se puede incluir letras

                  </label>

                    )}
                    {numberError === 2 && (

                <label className="text_error">

                  El nombre minimo es 3 caracteres

                </label>

                )}



                {numberError === 3 && (

                <label className="text_error">

                  El nombre maximo es 10 caracteres

                </label>

                )}
          
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Ingrese email" />
            <Form.Text className="text-muted">
              We'll never share your email with anyone else.
            </Form.Text>
          </Form.Group>
    
          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
            {numberError === 3 && (

<label className="text_error">

  El nombre maximo es 10 caracteres

</label>

)}
          </Form.Group>

          <Button  disabled={numberError>0} onClick={guardarClick} variant="primary">Primary</Button>{' '}

      </Form>

      
          )
  }

export default App
